var currentLevel = 0
var numberOfLevel = levels.length
var items
var table
var soluc
var showSoluce = false

var url = "qrc:/gcompris/src/activities/lightsoff/resource/"

function start(items_) {
    items = items_
    currentLevel = 0
    initLevel()
}

function stop() {}

function initLevel() {
    items.bar.level = currentLevel + 1
    table = levels[currentLevel].slice(0)
    soluc = solucs[currentLevel].slice(0)
    showSoluce = false
    items.modelTable = getTable()
    checkResult()
}

function nextLevel() {
    if (numberOfLevel <= ++currentLevel) {
        currentLevel = 0
    }
    initLevel()
}

function previousLevel() {
    if (--currentLevel < 0) {
        currentLevel = numberOfLevel - 1
    }
    initLevel()
}

function switchCandle(index) {
    /* Switch the selected item */
    table[index] = 1 - table[index]

    /* Swith the soluce */
    soluc[index] = 1 - soluc[index]

    /* Switch neighbor left */
    if (index % 5 !== 0)
        table[index - 1] = 1 - table[index - 1]

    /* Switch neighbor right */
    if (index % 5 !== 4)
        table[index + 1] = 1 - table[index + 1]

    /* Switch neighbor up */
    if (index > 4)
        table[index - 5] = 1 - table[index - 5]

    /* Switch neighbor down */
    if (index < 20)
        table[index + 5] = 1 - table[index + 5]

    /* Refresh the lights */
    items.modelTable = getTable()

    /* Check the result */
    checkResult()
}

function getTable() {
    var a=new Array();
    for (var i=0; i<25; ++i){
        a[i] = table[i] +(showSoluce ? 2 * soluc[i] : 0)
    }
    return a
}

function checkResult() {
    /* Check the result */
    var nb = 0
    table.forEach(function (entry) {
        nb += entry
    })

    items.skyColor = Qt.rgba(0, (127 - 127 * (25 - nb) / 25) / 255,
                             (255 - 255 * (25 - nb) / 25) / 255, 1)

    if (nb === 0) {
        items.bonus.good("tux")
    }
}

function solve() {
    showSoluce = !showSoluce

    /* Refresh the lights */
    items.modelTable = getTable()
}
